    private void btnHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHitungActionPerformed
        String ang1,pangkat;
        int hasil = 1;
        
        ang1 = txtAngka1.getText();
        pangkat = txtPangkat.getText();
        int a1 = Integer.parseInt(txtAngka1.getText());
        int a2 = Integer.parseInt(txtPangkat.getText());
        
        for(int i = 1; i <= a2; i++){
            hasil = hasil * a1;
        }
        txtHasil.setText(+a1+" pangkat "+a2+" = "+hasil);
    }//GEN-LAST:event_btnHitungActionPerformed

